import { FileEarmarkPdf } from 'react-bootstrap-icons';
import { Link } from 'react-router-dom';
import BookShelf from '../components/BookShelf';
import { useState, useEffect } from 'react';

export default function HomePage() {

    // shelves
    const shelf1 = "Currently Reading";
    const shelf2 = "Want to Read";
    const shelf3 = "Read";
    const shelves = [ shelf1, shelf2, shelf3]



    const [ books, setBooks ] = useState([{
        name: "To Kill a Mockingbird",
        author: "Harper Lee",
        imageURL: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api",
        shelf: shelf1
    },
    {
        name: "To Kill a Mockingbird",
        author: "Harper Lee",
        imageURL: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api",
        shelf: shelf2
    }])

    return (
    <div>
        <div className="list-books">
            <div className="list-books-title">
                <h1>My Reads</h1>
            </div>
            <div className="list-books-content">
                { shelves.map((shelf) => {
                    return <BookShelf 
                        key={shelf}    
                        shelf={shelf}
                        books={books.filter(book => book.shelf === shelf)}
                        />
                })}

            </div>
        <Link className="open-search" to="/search">Search</Link>
        </div>
    </div>
    )
}
