export default function ShelfPicker ({updateBook, bookID, shelves}) {

    function handleSelection(event) {
        event.preventDefault();
        let newShelf = event.target.value
        updateBook(bookID, newShelf)
    }

    return (
        <div>
        <div className="book-shelf-changer">
            <select onChange={(newShelf)=>handleSelection(newShelf)}>
                <option value="none" disabled>
                    Move to...
                </option>
                { shelves.map((shelf) => {
                    return (
                        <option value={shelf[0]} key={shelf[0]}>
                                {shelf[1]}
                        </option>
                    )
                })}
                <option value="none">None</option>
            </select>
        </div>
        </div>
    );
}
