import { Link } from 'react-router-dom';
import BookShelf from '../components/BookShelf';
import { useState, useEffect } from 'react';
import * as BooksAPI from "../utilities/BooksAPI";

export default function HomePage({shelves}) {

    // shelves
    const [ books, setBooks] = useState([])

    // call to get all books
    useEffect(() => {
        BooksAPI.getAll().then(response => setBooks(response))
      },[])

    function updateBook(book, newShelf) {
        console.log(book)
        console.log(newShelf)

        // trigger components re-render by updatinf the books 
        const newBooks = books.map(item => {
            if (item.id === book.id) {return {...item, shelf: newShelf};}
            return item;
        });
        setBooks(newBooks)

        // TODO API call, currently not working
        BooksAPI.update(book, newShelf).then(response => {
            console.log(response)
        })
    }

    return (
    <div>
        <div className="list-books">
            <div className="list-books-title">
                <h1>My Reads</h1>
            </div>
            <div className="list-books-content">
                { shelves.map((shelf) => {
                    return <BookShelf 
                        key={shelf[0]}    
                        shelf={shelf[1]}
                        shelfBooks={books.filter(book => book.shelf === shelf[0])}
                        updateBook={updateBook}
                        shelves={shelves}
                        />
                })}

            </div>
        <Link className="open-search" to="/search">Search</Link>
        </div>
    </div>
    )
}
