import serialize from "form-serialize";

export default function ShelfPicker ({updateBook, bookID, shelves}) {

    function handleSelection(event) {
        event.preventDefault();
        const bookUpdate = serialize(event.target, {hash: true})
        updateBook(bookUpdate)
    }

    return (
        <div>
        <div className="book-shelf-changer">
            <select>
                <option value="none" disabled>
                    Move to...
                </option>
                { shelves.map((shelf) => {
                    return (
                        <option value={shelf[0]} onClick={(bookID)=>handleSelection(bookID)} key={shelf[0]}>
                                {shelf[1]}
                        </option>
                    )
                })}
                <option value="none">None</option>
            </select>
        </div>
        </div>
    );
}
