import { Link } from 'react-router-dom';
import BookShelf from '../components/BookShelf';
import { useState, useEffect } from 'react';
import * as BooksAPI from "../utilities/BooksAPI";

export default function HomePage() {

    // shelves
    const shelf1 = ["currentlyReading", "Currently Reading"]
    const shelf2 = ["wantToRead", "Want to Read"]
    const shelf3 = ["read", "Read"]
    const shelves = [ shelf1, shelf2, shelf3]

    const [ allBooks, setAllBooks] = useState([])
    useEffect(() => {
        // first we define the getContacts function/ Why async?
        const getAllBooks = async() => {
          // define response const, it 
          const response = await BooksAPI.getAll(); 

          // update state
          setAllBooks(response);
        }; 

        // invoke the function
        getAllBooks();
      },[])


    console.log(allBooks)
    return (
    <div>
        <div className="list-books">
            <div className="list-books-title">
                <h1>My Reads</h1>
            </div>
            <div className="list-books-content">
                { shelves.map((shelf) => {
                    return <BookShelf 
                        key={shelf[0]}    
                        shelf={shelf[1]}
                        shelfBooks={allBooks.filter(book => book.shelf === shelf[0])}
                        />
                })}

            </div>
        <Link className="open-search" to="/search">Search</Link>
        </div>
    </div>
    )
}
