import * as BooksAPI from "../utilities/BooksAPI";

export default function HomePage() {

    // shelves
    const shelf1 = "Currently Reading";
    const shelf2 = "Want to Read";
    const shelf3 = "Read";
    const shelves = [ shelf1, shelf2, shelf3]



    const [ allBooks, setAllBooks ] = useState([{
        name: "To Kill a Mockingbird",
        author: "Harper Lee",
        imageURL: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api",
        shelf: shelf1
    },
    {
        name: "To Kill a Mockingbird",
        author: "Harper Lee",
        imageURL: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api",
        shelf: shelf2
    }])

    useEffect(() => {
        // first we define the getContacts function/ Why async?
        const getAllBooks = async() => {
          // define response const, it 
          const response = await BooksAPI.getAll(); 

          // update state
          setAllBooks(response);
