import { Link } from 'react-router-dom';
import BookShelf from '../components/BookShelf';
import { useState, useEffect } from 'react';
import * as BooksAPI from "../utilities/BooksAPI";

export default function HomePage() {

    // shelves
    const shelf1 = ["currentlyReading", "Currently Reading"]
    const shelf2 = ["wantToRead", "Want to Read"]
    const shelf3 = ["read", "Read"]
    const shelves = [ shelf1, shelf2, shelf3]

    const [ books, setBooks] = useState([])

    // call to get all books
    useEffect(() => {
        const getAllBooks = async() => {
          const response = await BooksAPI.getAll(); 
          setBooks(response);
        }; 
        getAllBooks();
      },[])

    function updateBook(newShelf, bookID) {
        // TODO API call, currently not working
        BooksAPI.update(bookID, newShelf)

        // trigger components re-render by updatinf the books 
        const newBooks = books.map(object => {
            if (object.id === bookID) {return {...object, shelf: newShelf};}
            return object;
        });
        setBooks(newBooks)
    }

    return (
    <div>
        <div className="list-books">
            <div className="list-books-title">
                <h1>My Reads</h1>
            </div>
            <div className="list-books-content">
                { shelves.map((shelf) => {
                    return <BookShelf 
                        key={shelf[0]}    
                        shelf={shelf[1]}
                        shelfBooks={books.filter(book => book.shelf === shelf[0])}
                        updateBook={updateBook}
                        shelves={shelves}
                        />
                })}

            </div>
        <Link className="open-search" to="/search">Search</Link>
        </div>
    </div>
    )
}
